  if(fieldValueTipoOrganigramaId == 0 && fieldValueOrganigramaContratanteId > 0)
  {
   	$('#contenedor_estructura').show();
    $('#contenedor_puesto').hide();
    $('#contenedor_puesto1').hide();
    $('#contenedor_nivel').hide();
    $('#contenedor_estructura1').hide();
  }

